mapper.select:
SELECT count(*)
FROM CourseSignUp
WHERE studentNumber = #{studentNumber}

mapper.update:
UPDATE CourseSignUp
SET 
	signUp = '0',
	signUpInserted = null
WHERE studentNumber = #{studentNumber}
AND classCode = #{classCode}

UPDATE CourseSignUp
SET 
	signUp = '1',
	signUpInserted = now()
WHERE studentNumber = #{studentNumber}
AND classCode = #{classCode}

mapper.delete:
DELETE FROM CourseSignUp
WHERE studentNumber = #{studentNumber}
AND classCode = #{classCode}

mapper.select:
SELECT classCode
FROM CourseSignUp
WHERE studentNumber = #{studentNumber}

SELECT 
	studentNumber,
    classCode,
    inserted,
    if(signUp, 'true', 'false') signUp,
    signUpInserted
FROM CourseSignUp
WHERE studentNumber = #{studentNumber}
AND classCode = #{classCode}
