mapper.select:
		SELECT count(*)
		FROM Course c
		LEFT JOIN Department d ON c.departmentId = d.id
		LEFT JOIN College l ON l.id = d.collegeId
        LEFT JOIN Organization o ON o.id = l.organizationId
		LEFT JOIN CourseInfo i ON c.classNumber = i.classNumber 
		LEFT JOIN CourseSchedule cs ON c.classCode = cs.classCode
        LEFT JOIN Professor p ON c.professorNumber = p.professorNumber	
		WHERE c.classCode > 0

			AND o.id = #{organizationId}


			AND l.id = #{collegeId}


			AND d.id = #{departmentId}


			AND c.year = #{year}


       		AND c.semester = #{semester}


        	AND c.grade = #{grade}


        	AND i.courseClassification = #{courseClassification}


        	AND c.classNumber = #{classNumber}


        	AND i.courseName LIKE #{courseName}


        	AND c.classroom LIKE #{classroom}


        	AND p.name LIKE #{professorName}

SELECT text
FROM SignUpNotice
WHERE id = #{id}

SELECT c.grade, i.courseClassification, c.classCode, c.classNumber, i.courseName, i.credit, i.theory, i.practice, c.maxPersonnel, d.name, c.year, c.semester, c.departmentId, d.name departmentName, d.collegeId, l.name collegeName, l.organizationId, o.name organizationName, cs.day, (SELECT ct.time FROM CourseTime ct WHERE ct.id = cs.startTimeId) startTime, (SELECT ct.time FROM CourseTime ct WHERE ct.id = cs.endTimeId) endTime, c.classroom, c.professorNumber, p.name professorName, p.contact professorContact, p.email professorEmail, p.homepage professorHomepage, p.inserted professorInserted, p.departmentId professorDepartmentId, pd.name professorDepartmentName, pd.collegeId professorCollegeId, pl.name professorCollegeName, pl.organizationId professorOragnizationId, po.name professorOrganizationName FROM Course c LEFT JOIN Department d ON c.departmentId = d.id LEFT JOIN College l ON l.id = d.collegeId LEFT JOIN Organization o ON o.id = l.organizationId LEFT JOIN CourseInfo i ON c.classNumber = i.classNumber LEFT JOIN CourseSchedule cs ON c.classCode = cs.classCode LEFT JOIN Professor p ON c.professorNumber = p.professorNumber LEFT JOIN Department pd ON p.departmentId = pd.id LEFT JOIN College pl ON pl.id = pd.collegeId LEFT JOIN Organization po ON po.id = pl.organizationId WHERE c.classCode > 0
AND o.id = #{organizationId}
AND l.id = #{collegeId}
AND d.id = #{departmentId}
AND c.year = #{year}
AND c.semester = #{semester}
AND c.grade = #{grade}
AND i.courseClassification = #{courseClassification}
AND c.classNumber = #{classNumber}
AND i.courseName LIKE #{courseName}
AND c.classroom LIKE #{classroom}
AND p.name LIKE #{professorName}
ORDER BY c.classCode LIMIT #{startNum}, #{count}

SELECT c.grade, i.courseClassification, c.classCode, c.classNumber, i.courseName, i.credit, i.theory, i.practice, c.maxPersonnel, d.name, c.year, c.semester, c.departmentId, d.name departmentName, d.collegeId, l.name collegeName, l.organizationId, o.name organizationName, GROUP_CONCAT(DISTINCT cs.day,' ', (SELECT ct.time FROM CourseTime ct WHERE ct.id = cs.startTimeId),'-', (SELECT ct.time FROM CourseTime ct WHERE ct.id = cs.endTimeId)) day, c.classroom, IF(s.classCode > 0, 'true', 'false') desire, s.classCode signUp, c.professorNumber, p.name professorName, p.contact professorContact, p.email professorEmail, p.homepage professorHomepage, p.inserted professorInserted, p.departmentId professorDepartmentId, pd.name professorDepartmentName, pd.collegeId professorCollegeId, pl.name professorCollegeName, pl.organizationId professorOragnizationId, po.name professorOrganizationName FROM Course c LEFT JOIN Department d ON c.departmentId = d.id LEFT JOIN College l ON l.id = d.collegeId LEFT JOIN Organization o ON o.id = l.organizationId LEFT JOIN CourseInfo i ON c.classNumber = i.classNumber LEFT JOIN CourseSchedule cs ON c.classCode = cs.classCode LEFT JOIN CourseSignUp s ON c.classCode = s.classCode AND s.studentNumber = #{studentNumber} LEFT JOIN Professor p ON c.professorNumber = p.professorNumber LEFT JOIN Department pd ON p.departmentId = pd.id LEFT JOIN College pl ON pl.id = pd.collegeId LEFT JOIN Organization po ON po.id = pl.organizationId WHERE c.classCode > 0
AND o.id = #{organizationId}
AND l.id = #{collegeId}
AND d.id = #{departmentId}
AND c.year = #{year}
AND c.semester = #{semester}
AND c.grade = #{grade}
AND i.courseClassification = #{courseClassification}
AND c.classNumber = #{classNumber}
AND i.courseName LIKE #{courseName}
AND c.classroom LIKE #{classroom}
AND p.name LIKE #{professorName}
GROUP BY c.classCode ORDER BY c.classCode LIMIT #{startNum}, #{count}
